(18, 2, 30, 31, 4, 1, 'หลักสูตรเดิม', '', '<style type="text/css">.headText{\r\n	background:#e4e4e4;\r\n	color:#e71d73;\r\n	font-weight:bold;\r\n	font-size:16px;\r\n	padding:5px;\r\n}\r\n\r\n.headText2{\r\n	color:#e71d73;\r\n	font-weight:bold;\r\n}\r\n</style>\r\n<table border="0" cellpadding="5" cellspacing="0" width="100%">\r\n	<tbody>\r\n		<tr>\r\n			<td valign="top" width="150"><img alt="" src="/uploads/yp-content/userfiles/images/course_ca_display.jpg" style="width: 150px; height: 600px;" /></td>\r\n			<td valign="top">\r\n			<div class="headText">ชื่อหลักสูตร</div>\r\n\r\n			<table bgcolor="#f6f6f6" border="0" cellpadding="3" cellspacing="0" width="100%">\r\n				<tbody>\r\n					<tr>\r\n						<td align="right" valign="top" width="250">ภาษาไทย :</td>\r\n						<td><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">หลักสูตรศิลปศาสตรบัณฑิต สาขาวิชานิเทศศาสตร์</span></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" valign="top">ภาษาอังกฤษ&nbsp;:</td>\r\n						<td><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">Bachelor of Arts Program in Communication Arts</span></td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			&nbsp;\r\n\r\n			<div class="headText">ชื่อปริญญา</div>\r\n\r\n			<table bgcolor="#f6f6f6" border="0" cellpadding="3" cellspacing="0" width="100%">\r\n				<tbody>\r\n					<tr>\r\n						<td align="right" valign="top" width="250">ภาษาไทย&nbsp;(ชื่อเต็ม) :<br />\r\n						(ชื่อย่อ) :</td>\r\n						<td valign="top"><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">ศิลปศาสตรบัณฑิต (นิเทศศาสตร์)&nbsp;</span><br style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);" />\r\n						<span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">ศศ.บ. (นิเทศศาสตร์)</span></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" valign="top">ภาษาอังกฤษ&nbsp;(ชื่อเต็ม) :</td>\r\n						<td valign="top"><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">Bachelor of Arts (Communication Arts)</span></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" valign="top">(ชื่อย่อ) :</td>\r\n						<td valign="top"><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">B.A. (Communication Arts)</span></td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			&nbsp;\r\n\r\n			<table bgcolor="#f6f6f6" border="0" cellpadding="3" cellspacing="0" width="100%">\r\n				<tbody>\r\n					<tr>\r\n						<td><span class="headText2">ปรัชญาของหลักสูตร</span><br />\r\n						&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">บัณฑิตสาขานิเทศศาสตร์เป็นผู้ที่มีความสามารถประยุกต์ใช้ความรู้และทักษะในวิชาชีพ ตลอดจนมีความตระหนักถึงมิติทางสังคม วัฒนธรรม และจริยธรรม เพื่อรังสรรค์ความเข้าใจอันดี และความเท่าเทียมทางการสื่อสารของกลุ่มต่าง ๆ ในสังคม</span><br />\r\n						<br />\r\n						<span class="headText2">วัตถุประสงค์ของหลักสูตร</span>\r\n\r\n						<ul style="margin-top:0px;">\r\n							<li><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">เพื่อผลิตบัณฑิตสาขานิเทศศาสตร์ที่มีความรู้และทักษะทางการสื่อสารและเทคโนโลยีการสื่อสาร</span></li>\r\n							<li><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">เพื่อให้บัณฑิตสามารถประยุกต์ใช้ความรู้และทักษะด้านนิเทศศาสตร์ในฐานะนักวิชาชีพและนักวิชาการที่ตระหนักถึงมิติทางสังคม วัฒนธรรม และจริยธรรม เพื่อสรรค์สร้างความเข้าใจอันดีและความเท่าเทียมทางการสื่อสารของกลุ่มต่าง ๆ ในสังคม</span></li>\r\n							<li><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">เพื่อให้บัณฑิตสามารถทำหน้าที่ด้านการสื่อสาร เพื่อตอบสนองต่อความต้องการทางสังคม บนพื้นฐานของความรู้ความเข้าใจในสังคมที่มีความหลากหลายทางวัฒนธรรม</span></li>\r\n						</ul>\r\n						</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			&nbsp;\r\n\r\n			<table bgcolor="#f6f6f6" border="0" cellpadding="3" cellspacing="0" width="100%">\r\n				<tbody>\r\n					<tr>\r\n						<td align="right" class="headText2" valign="top" width="250">จำนวนหน่วยกิตที่เรียนตลอดหลักสูตร :</td>\r\n						<td valign="top">132 หน่วยกิต</td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" class="headText2" valign="top">รูปแบบของหลักสูตร :</td>\r\n						<td><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">หลักสูตรระดับปริญญาตรี หลักสูตร 4 ปี</span></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" class="headText2" valign="top">สถานภาพของหลักสูตร :</td>\r\n						<td><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">หลักสูตรใหม่ พ.ศ.2544&nbsp;</span><span style="color: rgb(54, 54, 54); font-family: Tahoma; line-height: 16.890625px; background-color: rgb(247, 247, 247);">เริ่มใช้หลักสูตร ปีการศึกษา 2545 ถึงปีการศึกษา 2550</span></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" class="headText2" valign="top">โครงสร้างหลักสูตร/แผนการศึกษา :</td>\r\n						<td><a href="/uploads/yp-content/userfiles/files/sunisa/Course/CA/Structure_CA_2544.pdf">ดาวน์โหลดไฟล์</a></td>\r\n					</tr>\r\n					<tr>\r\n						<td align="right" class="headText2" valign="top">คำอธิบายรายวิชา :</td>\r\n						<td><a href="/uploads/yp-content/userfiles/files/sunisa/Course/CA/Explanation_CA_2544.pdf">ดาวน์โหลดไฟล์</a></td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, '', '2016-11-17 10:18:15', 1, 5, 1450344200, 5, 1450344200, NULL, NULL, NULL, NULL),
(19, 2, 33, 40, 3, 1, 'หลักสูตร ICT', '', '<table border="0" cellpadding="0" cellspacing="0" style="color:rgb(54, 54, 54); font-family:tahoma; height:100%; line-height:16.890625px; width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="vertical-align:top; width:30px">\r\n			<h3><img src="/uploads/yp-content/userfiles/images/ICT.jpg" style="border:0px; float:left; height:166px; margin-right:10px; opacity:0.9; width:250px" /></h3>\r\n			</td>\r\n			<td style="vertical-align:top">\r\n			<table border="0" cellpadding="1" cellspacing="1" style="width:500px">\r\n				<tbody>\r\n					<tr>\r\n						<td>\r\n						<h3><strong>หลักสูตรวิทยาศาสตรบัณฑิตสาขาเทคโนโลยีสารสนเทศและการสื่อสาร</strong><br />\r\n						<strong>Bachelor of Science Program in Information and&nbsp;<br />\r\n						Communication Technology</strong></h3>\r\n						</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n\r\n			<h3><span style="font-size:12px">บัณฑิตสาขาเทคโนโลยีสารสนเทศและการสื่อสารเป็นผู้ที่มีความสามารถ<br />\r\n			ประยุกต์ใช้ความรู้และทักษะด้านเทคโนโลยีสารสนเทศและการสื่อสาร<br />\r\n			ในการพัฒนาองค์กรทุกระดับด้วยความตระหนักถึงวัฒนธรรมขององค์กร</span><br />\r\n			<span style="font-size:12px">มิติทางสังคม คุณธรรมจริยธรรมจรรยาบรรณในวิชาชีพด้าน</span><span style="font-size:12px">เทคโนโลยีสารสนเทศ<br />\r\n			และพร้อมก้าวสู่ประชาคมอาเซียน</span></h3>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="4" cellspacing="4" style="color:rgb(54, 54, 54); font-family:tahoma; height:100%; line-height:1.6em; width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="4" style="background-color:rgb(204, 204, 204)"><strong><span style="font-size:14px">เรียนอะไรใน......เทคโนโลยีสารสนเทศและการสื่อสาร</span></strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:20px"><strong><span style="color:rgb(0, 0, 255)"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/bull_orange01.gif" style="border:0px; height:13px; opacity:0.9; width:15px" /></span></span></s