INSERT INTO `web_page` (`id`, `root`, `lft`, `rgt`, `level`, `status`, `title`, `slug`, `content`, `hits`, `image`, `published_at`, `version`, `created_by`, `created_at`, `updated_by`, `updated_at`, `meta_title`, `meta_keywords`, `meta_description`, `deleted_at`) VALUES
(45, 2, 88, 101, 2, 1, 'การบริการวิชาการ', '', '<table border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="5" style="text-align:center"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/policy%20service.jpg" style="height:131px; width:550px" /></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="5" style="background-color:rgb(255, 204, 153)">\r\n			<p><span style="font-size:14px"><span style="color:#B22222"><strong>1. นโยบายและวัตถุประสงค์ของการให้บริการวิชาการ</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="5">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="5"><span style="font-size:12px"><span style="color:rgb(230, 56, 19)"><strong>&nbsp; &nbsp;<span style="font-size:14px"> 1.1 นโยบายด้านการบริการวิชาการ</span></strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td colspan="3" rowspan="2"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/resize%20DSC_0972%20(1024x680).jpg" style="border-style:solid; border-width:1px; height:170px; line-height:1.6em; opacity:0.9; width:256px" /></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p style="margin-left:40px; text-align:justify"><span style="font-size:12px">ภารกิจหลักที่สำคัญภารกิจหนึ่งของคณะวิทยาการสื่อสาร มหาวิทยาลัยสงขลานครินทร์ คือ การให้บริการทางวิชาการแก่สังคม ตามเป้าหมายที่กำหนดไว้ในด้านการบริการวิชาการของมหาวิทยาลัย ที่จะให้เป็นมหาวิทยาลัยที่เอื้ออาทรต่อการพัฒนาชุมชน เป็นที่พึ่งของชุมชน โดยการสร้างศักยภาพของชุมชนให้มีความเข้มแข็ง มีความยั่งยืน ผ่านกระบวนการเรียนการสอน การบริการวิชาการและการวิจัย โดยการสร้างศักยภาพให้ชุมชนสามารถเข้าถึงแหล่งเรียนรู้ได้ตลอดชีวิต&nbsp;</span></p>\r\n\r\n			<p style="margin-left:40px; text-align:justify"><span style="font-size:12px">คณะวิทยาการสื่อสาร มหาวิทยาลัยสงขลานครินทร์ วิทยาเขตปัตตานี จึงได้กำหนดพันธกิจที่มุ่งเน้นด้านการให้บริการวิชาการแก่ชุมชน ในการพัฒนาคณะให้เป็นศูนย์กลางการผลิตสื่อและบริการวิชาการด้านวิทยาการสื่อสารในภูมิภาคเพื่อให้การบริการทางวิชาการของคณะวิทยาการสื่อสาร สามารถดำเนินการตามภารกิจหลักให้สอดคล้องกับกลุ่มเป้าหมาย และนโยบายที่มหาวิทยาลัยได้กำหนดไว้</span></p>\r\n			</td>\r\n			<td style="width:20px"><span style="font-size:12px">&nbsp; &nbsp; &nbsp;&nbsp;</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2">&nbsp;</td>\r\n			<td colspan="3">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="5"><span style="font-size:12px"><span style="color:rgb(230, 56, 19)"><strong>&nbsp; &nbsp; <span style="font-size:14px">1.2 วัตถุประสงค์ของการให้บริการวิชาการแก่ชุมชน</span></strong></span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" rowspan="1">\r\n			<p style="margin-left:40px"><span style="font-size:12px">เพื่อพัฒนาและส่งเสริมให้ชุมชนมีศักยภาพในการสื่อสารและการใช้เทคโนโลยีสารสนเทศ&nbsp;โดยการสร้างเครือข่าย การเรียนรู้ร่วมกันระหว่างสถาบันการศึกษาและชุมชน</span></p>\r\n			</td>\r\n			<td style="width:20px">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="background-color:rgb(255, 204, 153)">\r\n			<p><span style="font-size:14px"><span style="color:#B22222"><strong>2. แผนปฏิบัติการ (Action Plan) ด้านบริการวิชาการของคณะฯ&nbsp; </strong></span></span></p>\r\n			</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; คณะวิทยาการสื่อสารได้กำหนดแผนยุทธศาสตร์ &ldquo;สร้างสรรค์วิชาการ บริการสู่สังคม สั่งสมความรู้สู่งานวิจัย&rdquo; เพื่อให้สอดคล้องกับพันธกิจด้านบริการวิชาการของคณะฯ และได้กำหนดแผนปฏิบัติการด้านการวิจัยและบริการวิชาการ ประจำปีงบประมาณ 2554 &ndash; 2558 ดังนี้</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style="font-size:14px"><strong>เป้าประสงค์ที่ 1&nbsp;พัฒนาคณะให้เป็นศูนย์กลางการผลิตสื่อและบริการวิชาการในภูมิภาคด้านวิทยาการสื่อสาร</strong></span></p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td rowspan="2" style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px"><span style="font-size:12px"><strong><span style="color:#B22222">กลยุทธ์</span></strong></span></td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td rowspan="2" style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px"><span style="font-size:12px"><span style="color:#B22222"><strong>กิจกรรม/โครงการ</strong></span></span></td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td rowspan="2" style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px"><span style="font-size:12px"><span style="color:#B22222"><strong>ตัวชี้วัดความสำเร็จ</strong></span></span></td>\r\n			<td style="text-align:center; width:5px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; width:5px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">\r\n			<p><span style="font-size:12px">1. สำรวจความต้องการบริการวิชาการของชุมชนและมุ่งเน้นกลุ่มเป้าหมายที่หลากหลายเพื่อนำมาใช้เป็นข้อมูลในการวางแผนและพัฒนาระบบการบริหารงานบริการวิชาการ</span></p>\r\n			</td>\r\n			<td rowspan="6" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">\r\n			<p style="text-align:justify"><span style="font-size:12px">1.&nbsp;ออกแบบสอบถามเพื่อสำรวจความต้องการจากการให้บริการวิชาการของคณะ</span></p>\r\n			</td>\r\n			<td rowspan="6" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify; vertical-align:middle"><span style="font-size:12px">1.&nbsp;สำรวจความต้องการบริการวิชาการของชุมชนอย่างน้อย 2 ปี/ครั้ง</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify"><span style="font-size:12px">2.&nbsp;ออกสำรวจความต้องการของชุมชนโดยการเชิญผู้ที่เกี่ยวข้องกับการให้บริการวิชาการของคณะร่วมแสดงความคิดเห็น</span></td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify"><span style="font-size:12px">1.&nbsp;มีการประเมินผลความสำเร็จของแผนงานบริการวิชาการที่เป็นระบบอย่างน้อยปีละ 1 ครั้ง &nbsp; &nbsp;&nbsp;</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td colspan="1" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1" rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">2. การบูรณาการวิชาการกับการจัดการเรียนการสอนและการวิจัย</span>\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td colspan="1" rowspan="3" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">1.&nbsp;จัดอบรมวิจัยในชั้นเรียน &nbsp; &nbsp; &nbsp;<br />\r\n			&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">1. งานวิจัยในชั้นเรียน</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">2. นักศึกษามีส่วนร่วมในกิจกรรมบริการวิชาการของคณะ&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></td>\r\n			<td rowspan="2">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="font-size:13px; line-height:1.6em; width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="5">\r\n			<hr /></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:20px">&nbsp;</td>\r\n			<td colspan="2" style="text-align:justify">&nbsp;</td>\r\n			<td style="width:30px">&nbsp;</td>\r\n			<td colspan="1">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="width:20px">&nbsp;</td>\r\n			<td colspan="2" rowspan="1" style="text-align:justify">\r\n			<p><span style="font-size:12px">เมื่อปี พ.ศ.2555&nbsp;ได้ดำเนินกิจกรรมด้านบริการวิชาการตามแผนปฏิบัติการคณะวิทยาการสื่อสาร โดยได้ดำเนินการสำรวจความต้องการบริการวิชาการ ผ่านแบบสอบถามเพื่อประเมินโครงการบริการวิชาการที่คณะฯ ได้ดำเนินโครงการ โดยได้แทรกข้อคำถามในประเด็นโครงการอื่น ๆ ที่คณะฯ ควรจัดในโอกาสต่อไป เพื่อเป็นการสำรวจความต้องการรับบริการวิชาการ ผ่านกลุ่มตัวอย่างจากผู้เข้าร่วมโครงการบริการวิชาการในปีพ.ศ.&nbsp;2555&nbsp;และนำข้อมูลผลประเมินที่ได้ไปนำเสนอคณะกรรมการบริหารงานบริการวิชาการ เพื่อใช้ในการวางแผนและพัฒนาระบบบริหารงานบริการวิชาการในปีพ.ศ.2556 ต่อไป</span></p>\r\n			</td>\r\n			<td style="width:30px">&nbsp;</td>\r\n			<td colspan="1"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/DSC_2663.JPG" style="border-style:solid; border-width:1px; height:175px; width:256px" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1">&nbsp;</td>\r\n			<td colspan="2" style="text-align:justify">\r\n			<p><span style="font-size:12px">นอกจากนี้ คณะฯ ได้ส่งเสริมให้มีการบูรณาการบริการวิชาการกับการจัดการเรียนการสอน โดยส่งเสริมให้นักศึกษามีส่วนร่วมในกิจกรรมบริการวิชาการของคณะ เช่น การเข้าร่วมโครงการเพื่อเพิ่มพูนความรู้นอกเหนือ จากกิจกรรมการเรียนการสอน&nbsp;และการส่งเสริมให้นักศึกษาเข้ามามีส่วนร่วมเป็นผู้ช่วยวิทยากรและผู้ช่วยกิจกรรม เพื่อให้นักศึกษาได้นำความรู้ที่ได้เรียนมาถ่ายทอดให้กับผู้เข้าร่วมโครงการต่อไป</span></p>\r\n			</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n			<td style="border-color:rgb(204, 204, 204)"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/cropDSC_3733.JPG" style="border-style:solid; border-width:1px; font-size:11.818181991577148px; height:179px; opacity:0.9; text-align:justify; width:256px" /></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style="font-size:14px"><strong>เป้าประสงค์ที่ 2&nbsp;เสริมสร้างศักยภาพงานบริการวิชาการสู่งานวิจัย</strong></span></p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1">\r\n	<tbody>\r\n		<tr>\r\n			<td style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px">\r\n			<p><span style="font-size:12px"><span style="color:#B22222"><strong>กลยุทธ์</strong></span></span></p>\r\n			</td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px"><span style="font-size:12px"><span style="color:#B22222"><strong>กิจกรรม/โครงการ</strong></span></span></td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:center; width:1px">&nbsp;</td>\r\n			<td style="background-color:rgb(255, 204, 153); border-color:rgb(255, 204, 153); text-align:center; width:300px"><span style="font-size:12px"><span style="color:#B22222"><strong>ตัวชี้วัดความสำเร็จ</strong></span></span></td>\r\n			<td style="text-align:center; width:5px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1" rowspan="2" style="border-color:rgb(255, 204, 153); text-align:justify; vertical-align:top">&nbsp;</td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></td>\r\n			<td rowspan="2" style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan="8" style="border-color:rgb(255, 204, 153); text-align:justify; width:1px">&nbsp;</td>\r\n			<td colspan="1" rowspan="8" style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1" rowspan="2" style="border-color:rgb(255, 204, 153); text-align:justify; vertical-align:top"><span style="font-size:12px">1.&nbsp;ออกแบบงานบริการวิชาการที่นำไปสู่งานวิจัย และงานวิจัยเอื้อไปสู่การให้บริการวิชาการแก่ชุมชน</span></td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify"><span style="font-size:12px">1.&nbsp;จัดเสวนาการบูรณาการบริการวิชาการและการเรียนการสอนสู่งานวิจัย</span></td>\r\n			<td colspan="1" style="border-color:rgb(255, 204, 153)"><span style="font-size:12px">1. จะต้องมีการนำข้อมูลจากการให้บริการวิชาการมาใช้ในงานวิจัย &nbsp;&nbsp;</span></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify"><span style="font-size:12px">2.&nbsp;มีงานวิจัยที่มีนักศึกษามีส่วนร่วมในการทำวิจัย</span></td>\r\n			<td colspan="1" style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top">&nbsp;</td>\r\n			<td colspan="1" style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="1" rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">2. One Major One Project : OMOP โครงการบริการวิชาการเฉพาะสาขา &nbsp;&nbsp;</span></td>\r\n			<td colspan="1" rowspan="2" style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">1.&nbsp;จัดโครงการบริการวิชาการโดยแต่ละสาขาเสนอโครงการเข้าคณะ &nbsp;&nbsp;</span></td>\r\n			<td style="border-color:rgb(255, 204, 153)"><span style="font-size:12px">1. จำนวนอาจารย์ที่ให้บริการวิชาการอย่างน้อยสาขาละ 2 คน&nbsp;</span></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify"><span style="font-size:12px">2. ผู้รับบริการมีความพอใจ ร้อยละ 80</span></td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:justify">&nbsp;</td>\r\n			<td style="text-align:justify">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">3. สร้างเครือข่ายความร่วมมือกับองค์กรวิชาการในการให้บริการวิชาการแก่ชุมชน &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">1. จัดเวทีเสวนาแลกเปลี่ยนเรียนรู้กับผู้มีส่วนได้ส่วนเสียในการให้บริการวิชาการแก่ชุมชน</span></td>\r\n			<td style="border-color:rgb(255, 204, 153); text-align:left; vertical-align:top"><span style="font-size:12px">1.&nbsp;มีข้อมูลเครือข่ายองค์กรวิชาชีพที่ร่วมมือกับคณะอย่างเป็นรูปธรรม &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<table border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:10px">&nbsp;</td>\r\n			<td><span style="font-size:12px">คณะฯ ได้จัดโครงการเวทีเสวนาแลกเปลี่ยนเรียนรู้การให้บริการวิชาการแก่ชุมชน โดยกำหนดเป็น 2&nbsp;กิจกรรม คือ</span></td>\r\n			<td style="width:20px">&nbsp;</td>\r\n			<td colspan="1" rowspan="3"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/resize_DSC_5781(1).JPG" style="border-style:solid; border-width:1px; font-size:11.818181991577148px; height:170px; opacity:0.9; width:256px" /></span></td>\r\n			<td style="width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:justify"><span style="font-size:12px">กิจกรรม แลกเปลี่ยนเรียนรู้การให้บริการวิชาการแก่ชุมชน โดยได้เชิญวิทยากรคณะวิทยาศาสตร์และเทคโนโลยี ซึ่งได้รับรางวัลอาจารย์ตัวอย่างของมหาวิทยาลัยสงขลานครินทร์ประจำปี 2555 ด้านบริการวิชาการ มาให้ความรู้ในหัวข้อ&ldquo;บริการวิชาการอย่างไร...ให้ได้ใจผู้รับบริการ&rdquo;เพื่อแลกเปลี่ยนเรียนรู้ประสบการณ์ด้านการให้บริการวิชาการแก่ชุมชน และการบูรณาการบริการวิชาการและการเรียนการสอนสู่งานวิจัย เมื่อวันที่ 14 พฤศจิกายน 2555 </span></td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>&nbsp;</td>\r\n			<td style="text-align:justify"><span style="font-size:12px">และอีกกิจกรรมหนึ่ง ได้จัดกิจกรรมเวทีเสวนาแลกเปลี่ยนเรียนรู้ในการให้บริการวิชาการแก่ชุมชน ในวันที่ 27กุมภาพันธ์ 2556ที่ผ่านมาเพื่อสำรวจความต้องการของชุมชน ภาครัฐหรือภาคเอกชน หรือหน่วยงานวิชาชีพ เปิดโอกาสให้ผู้มีส่วนร่วมจากชุมชนหรือหน่วยงานที่เกี่ยวข้องกับวิชาชีพที่จะให้บริการวิชาการมาร่วมแลกเปลี่ยนเรียนรู้ และร่วมมือรวมพลังในลักษณะของการสร้างเครือข่ายความร่วมมือกับองค์กรวิชาการในการให้บริการวิชาการแก่ชุมชน และเป็นการประเมินประโยชน์หรือผลกระทบของการให้บริการวิชาการของคณะฯ ให้สอดคล้องกับความต้องการของผู้รับบริการทั้งโดยทางตรงหรือทางอ้อม (คลิก&gt;&gt;อ่านรายงานผล)</span></td>\r\n			<td>&nbsp;</td>\r\n			<td><a href="/?name=photos&amp;file=view&amp;t=1361953948" style="font-size: 12px;" target="_blank"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/DSC_0601.JPG" style="border-style:solid; border-width:1px; height:169px; opacity:0.9; width:256px" /></a></td>\r\n			<td>&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '', '2016-11-17 10:18:15', 1, 5, 1450344200, 5, 1450344200, NULL, NULL, NULL, NULL),
(46, 2, 89, 90, 3, 1, 'การให้บริการวิชาการแก่ชุมชน', '', '<table border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top">\r\n			<div style="text-align: center;"><span style="font-size:12px"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/type%20service.jpg" style="height:131px; width:550px" /></span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top">\r\n			<div style="text-align: justify;"><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; คณะวิทยาการสื่อสาร มหาวิทยาลัยสงขลานครินทร์ วิทยาเขตปัตตานี ได้ให้บริการวิชาการแก่ชุมชนในรูปแบบต่าง ๆ อาทิ การฝึกอบรม </span><span style="font-size:12px">บริการห้องปฏิบัติการและอุปกรณ์โสตทัศนูปกรณ์&nbsp;</span><span style="font-size:12px">การรับเชิญเป็นวิทยากร การให้คำปรึกษาแก่ชุมชน&nbsp;</span><span style="font-size:12px">แก่หน่วยงานของรัฐและบุคคลทั่วไป&nbsp;</span></div>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n			<td colspan="8" style="text-align:left; vertical-align:top">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<div>\r\n			<p><span style="color:#B22222"><span style="font-size:14px"><strong>การให้บริการวิชาการแก่ชุมชน</strong></span></span></p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp;</span></div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;คณะวิทยาการสื่อสารมีการให้บริการทางวิชาการโดยแบ่งเป็นประเภทต่างๆได้ดังนี้</span></div>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<div>\r\n			<p><span style="font-size:14px"><span style="color:#B22222"><strong>1. บริการห้องปฏิบัติการและอุปกรณ์ปฏิบัติการ</strong></span></span></p>\r\n			</div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="6" rowspan="1" style="text-align:left; vertical-align:top; width:100%"><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; คณะวิทยาการสื่อสาร ให้บริการเช่าห้องปฏิบัติการ/ห้องบรรยาย/ห้องประชุม และอุปกรณ์ปฏิบัติการ</span><span style="font-size:12px">ที่มีประสิทธิภาพในการใช้งานแก่บุคลากรและนักศึกษาภายในมหาวิทยาลัยและหน่วยงานภายนอกมหาวิทยาลัย&nbsp;</span><span style="font-size:12px">โดยมีรายละเอียดดังต่อไปนี้</span></td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" rowspan="3" style="text-align:left; vertical-align:top; width:100%">\r\n			<ol>\r\n				<li style="text-align:left"><span style="font-size:12px">ห้องปฏิบัติการมัลติมีเดียพร้อมเครื่องคอมพิวเตอร์</span></li>\r\n				<li><span style="font-size:12px">ห้องประชุมและห้องบรรยาย</span></li>\r\n				<li style="text-align:justify"><span style="font-size:12px">ห้องปฏิบัติการวิทยุกระจายเสียง&nbsp; บริการด้านการบันทึกเสียง&nbsp; อัดสปอตโฆษณา เป็นต้น</span></li>\r\n				<li style="text-align:justify"><span style="font-size:12px">ห้องปฏิบัติการวิทยุโทรทัศน์ ให้บริการถ่ายทำเทปโทรทัศน์&nbsp; การบันทึกเทป การตัดต่อและผลิตสื่อ</span></li>\r\n				<li style="text-align:justify"><span style="font-size:12px">อุปกรณ์ปฏิบัติการ ให้บริการโสตทัศนูปกรณ์ และอุปกรณ์การถ่ายภาพ บันทึกภาพและเสียง</span></li>\r\n			</ol>\r\n\r\n			<div style="margin-left: 40px;">\r\n			<div>&nbsp;</div>\r\n			</div>\r\n			</td>\r\n			<td style="text-align:left; vertical-align:top; width:30px">&nbsp;</td>\r\n			<td style="text-align:left; vertical-align:top; width:20%">&nbsp;</td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:30px">&nbsp;</td>\r\n			<td colspan="1" rowspan="2" style="text-align:left; vertical-align:top; width:20%"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/crop_DSC_0588.JPG" style="border-style:solid; border-width:1px; font-size:12px; height:165px; line-height:1.6em; opacity:0.9; width:250px" /></td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:30px">&nbsp; &nbsp; &nbsp;</td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<p><span style="color:#B22222"><span style="font-size:14px"><strong>2. บริการฝึกอบรม</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">\r\n			<p><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; คณะวิทยาการสื่อสารเป็นคณะวิชาที่เน้นการบูรณาการศาสตร์แห่งการสื่อสารคือ &quot;นิเทศศาสตร์&quot;&nbsp;กับ&nbsp;</span><span style="font-size:12px">&quot;เทคโนโลยีสารสนเทศและการสื่อสาร&quot;&nbsp;เปิดสอน 3&nbsp;สาขาวิชา ได้แก่ สาขานิเทศศาสตร์&nbsp;สาขาเทคโนโลยี</span><span style="font-size:12px">สารสนเทศและการสื่อสาร&nbsp;และสาขาวิชานวัตกรรมการออกแบบและสร้างสรรค์สื่อ&nbsp;</span><span style="font-size:12px">โดยคณะฯ ได้มีโครงการบริการวิชาการครอบคลุมสาขาวิชาดังกล่าว ดังต่อไปนี้</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:10px">\r\n			<div style="text-align: center;"><span style="font-size:12px">1.&nbsp;</span></div>\r\n			</td>\r\n			<td colspan="3" rowspan="1" style="text-align:left; vertical-align:top; width:100%"><span style="font-size:12px">ด้านนิเทศศาสตร์</span></td>\r\n			<td colspan="2" style="text-align:left; vertical-align:top; width:20%">&nbsp;</td>\r\n			<td colspan="11" rowspan="11" style="text-align:left; vertical-align:top; width:20px"><br />\r\n			<span style="font-size:12px">&nbsp; &nbsp; &nbsp;&nbsp;</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:5%">\r\n			<div><span style="font-size:12px">&nbsp; &nbsp;&nbsp;</span></div>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">1.1 &nbsp;</span></td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%"><span style="font-size:12px">ด้านสื่อสารมวลชน เช่น โครงการบริการวิชาการชุมชนเคลื่อนที่&ldquo;รู้เท่าทันสื่อ&rdquo;&nbsp;และโครงการอบรม &ldquo;ความรู้เท่าทันสื่อและข่าวสารสำหรับครู 3 จังหวัดชายแดนภาคใต้&rdquo;</span></td>\r\n			<td rowspan="10" style="text-align:left; vertical-align:top; width:40px">&nbsp;</td>\r\n			<td rowspan="10" style="text-align:left; vertical-align:top; width:10%">\r\n			<p><img alt="" src="/uploads/yp-content/userfiles/images/oratai/Commsci%20TV.JPG" style="border-style:solid; border-width:1px; height:167px; width:250px" /></p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt="" src="/uploads/yp-content/userfiles/images/oratai/crop_labcom4.JPG" style="border-style:solid; border-width:1px; font-size:12px; height:166px; line-height:1.6em; opacity:0.9; width:250px" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" rowspan="1" style="text-align:left; vertical-align:top; width:5%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">&nbsp; &nbsp; &nbsp;</span></td>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">1.2</span></td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%"><span style="font-size:12px">ด้านวารสารศาสตร์ เช่น อบรมเทคนิคการเขียนเชิงวารสารศาสตร์, อบรมผู้สื่อข่าวชุมชน,&nbsp;อบรมเยาวชนคนข่าว, อบรม &ldquo;Photoshop&rdquo; และโครงการอบรมเชิงปฏิบัติการ Photo Trip:&nbsp;ทัศนศึกษาเพื่อสื่อสารเรื่องราวอย่างสร้างสรรค์ผ่านภาพถ่าย</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="text-align:left; vertical-align:top; width:5%">&nbsp;</td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">&nbsp; &nbsp; &nbsp;</span></td>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">1.3</span></td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%"><span style="font-size:12px">ด้านวิทยุกระจายเสียงและวิทยุโทรทัศน์ เช่น อบรมการตัดต่อวีดีทัศน์โดยใช้โปรแกรม Adobe Premiere,&nbsp;อบรมเชิงปฏิบัติการเรื่อง &ldquo;การผลิตสารคดีโทรทัศน์ฝึกปฏิบัติสำหรับเยาวชนชายแดนใต้&rdquo;,&nbsp;อบรมเชิงปฏิบัติการเรื่อง &ldquo;ก้าวต่อไปของวิทยุชุมชนจังหวัดชายแดนภาคใต้&rdquo;และการอบรมเรื่อง &ldquo;การตัดต่อวีดิทัศน์</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="2" style="text-align:left; vertical-align:top; width:5%">&nbsp;</td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">&nbsp; &nbsp; &nbsp;</span></td>\r\n			<td style="text-align:left; vertical-align:top; width:5%"><span style="font-size:12px">1.4&nbsp;</span></td>\r\n			<td colspan="2" style="text-align:justify; vertical-align:top; width:100%"><span style="font-size:12px">ด้านการสื่อสารการตลาด เช่น อบรมเชิงปฏิบัติการเรื่อง &ldquo;กลยุทธ์การประชาสัมพันธ์ทางธุรกิจสำหรับผู้ประกอบการขนาดเล็ก&rdquo;</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" style="text-align:justify; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:top; width:10px"><span style="font-size:12px">2.&nbsp;</span></td>\r\n			<td colspan="3" style="text-align:justify; vertical-align:top; width:100%"><span style="font-size:12px">ด้านเทคโนโลยีสารสนเทศและการสื่อสารเช่น โครงการอบรมเชิงปฏิบัติการเรื่อง &quot;ITพี่สอนน้อง&quot; &nbsp;</span><span style="font-size:12px">และโครงการไอทีกับการสืบค้นและจัดเก็บข้อมูลเพื่อการบริหารงานส่วนท้องถิ่นใน 3 จังหวัดชายแดนภาคใต้&quot;</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="text-align:center; vertical-align:top; width:10px"><span style="font-size:12px">3.&nbsp;</span></td>\r\n			<td colspan="5" rowspan="1" style="text-align:left; vertical-align:top; width:100%"><span style="font-size:12px">ด้านนวัตกรรมการออกแบบและสร้างสรรค์สื่อเช่น โครงการสื่อการเรียนรู้สันติวิธีในจังหวัดชายแดนภาคใต้ เป็นต้น&nbsp;</span></td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">\r\n			<p style="text-align:justify"><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; นอกจากนี้ คณะวิทยาการสื่อสาร ยังได้จัดโครงการ/กิจกรรมเพื่อส่งเสริมและทำนุบำรุงศิลปวัฒนธรรม&nbsp;ส่งเสริมการอนุรักษ์สิ่งแวดล้อม และส่งเสริมความสัมพันธ์ที่ดีต่อชุมชน เช่น โครงการ &ldquo;ตามรอยวิถีชุมชน&rdquo;,&nbsp;โครงการนักสื่อสารพัฒนาชุมชนตอนสร้างฝายถวายพ่อหลวงและโครงการธารน้ำใจ เป็นต้น&nbsp;</span></p>\r\n\r\n			<div style="margin-left: 40px;"><span style="font-size:12px">&nbsp; &nbsp;</span></div>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<p><span style="color:#B22222"><span style="font-size:14px"><strong>3. บริการให้คำปรึกษาแก่ชุมชน</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" style="text-align:left; vertical-align:top; width:100%">\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp;</span></div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp;คณะวิทยาการสื่อสารมีบริการให้คำปรึกษาแก่ชุมชน ในหัวข้อ/เรื่องดังต่อไปนี้</span></div>\r\n\r\n			<ol>\r\n				<li style="text-align:justify">\r\n				<p><span style="font-size:12px">ด้านนิเทศศาสตร์และการสื่อสารมวลชน ได้แก่ การผลิตสื่อประชาสัมพันธ์ของชุมชน &nbsp;การจัดและผลิตรายการวิทยุโทรทัศน์ การสื่อสารในกลุ่ม &nbsp;การสื่อสารเพื่อการโน้มน้าวใจ และการถ่ายภาพเพื่อการสื่อสารมวลชน</span></p>\r\n				</li>\r\n				<li style="text-align:justify">\r\n				<p><span style="font-size:12px">ด้านเทคโนโลยีสารสนเทศและการสื่อสารได้แก่ การออกแบบเว็บไซต์ &nbsp;การออกแบบฐานข้อมูล</span></p>\r\n				</li>\r\n				<li style="text-align:justify">\r\n				<p><span style="font-size:12px">ด้านสื่อสารการตลาดเพื่อการพัฒนาได้แก่ การวางแผนการตลาด</span></p>\r\n				</li>\r\n				<li style="text-align:justify">\r\n				<p><span style="font-size:12px">ด้านการออกแบบสื่อและผลิตสื่อได้แก่ การออกแบบบรรจุภัณฑ์ &nbsp;การผลิตหนังสั้นและการ์ตูนแอนิเมชัน</span></p>\r\n				</li>\r\n				<li style="text-align:justify">\r\n				<p><span style="font-size:12px">ด้านการจัดกิจกรรมของชุมชน &nbsp;ได้แก่ การให้คำปรึกษาด้านทักษะการทำงานร่วมกัน ภาวะผู้นำ &nbsp;การสื่อสารในกลุ่มเพื่อการพัฒนา &nbsp;การผลิตสื่อชุมชน &nbsp;และการรณรงค์ในชุมชน</span></p>\r\n				</li>\r\n			</ol>\r\n\r\n			<div style="margin-left: 40px;">&nbsp;</div>\r\n			</td>\r\n			<td style="text-align:left; vertical-align:top; width:30px">&nbsp;</td>\r\n			<td style="text-align:left; vertical-align:top; width:20%">\r\n			<p>&nbsp;</p>\r\n\r\n			<p><img alt="" src="/uploads/yp-content/userfiles/images/oratai/6june2013.jpg" style="border-style:solid; border-width:1px; font-size:12px; height:166px; line-height:1.6em; opacity:0.9; width:250px" /></p>\r\n			</td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<p><span style="color:#B22222"><span style="font-size:14px"><strong>4. วิทยากรตามคำเชิญของชุมชน/หน่วยงานภายนอก</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="4" rowspan="1" style="text-align:left; vertical-align:top; width:100%">\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp;</span></div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp;คณะวิทยาการสื่อสารมีบุคลากรที่มีความเชี่ยวชาญในสาขาดังต่อไปนี้</span></div>\r\n\r\n			<p style="margin-left:40px"><span style="font-size:12px">1. อาจารย์ประจำสาขาวิชานิเทศศาสตร์</span></p>\r\n\r\n			<p style="margin-left:40px"><span style="font-size:12px">2. อาจารย์ประจำสาขาวิชาเทคโนโลยีสารสนเทศและการสื่อสาร</span></p>\r\n\r\n			<p style="margin-left:40px"><span style="font-size:12px">3. อาจารย์ประจำสาขาวิ</span><span style="font-size:12px">ชานวัตกรรมการออกแบบและสร้างสรรค์สื่อ</span></p>\r\n\r\n			<div style="margin-left: 40px;">&nbsp;</div>\r\n			</td>\r\n			<td colspan="2" style="text-align:left; vertical-align:top"><img alt="" src="/uploads/yp-content/userfiles/images/oratai/DSC_1400.JPG" style="border-style:solid; border-width:1px; font-size:12px; height:166px; line-height:1.6em; opacity:0.9; width:250px" /></td>\r\n			<td colspan="11" style="text-align:left; vertical-align:top; width:20px">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="background-color:rgb(255, 204, 204); text-align:left; vertical-align:top; width:100%">\r\n			<p><span style="color:#B22222"><span style="font-size:14px"><strong>5. การติดต่อ/สอบถามด้านการให้บริการวิชาการของคณะฯ</strong></span></span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan="17" style="text-align:left; vertical-align:top; width:100%">\r\n			<div>&nbsp;</div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &gt;&gt;&nbsp;บริการห้องปฏิบัติการและอุปกรณ์ปฏิบัติการ/บริการฝึกอบรม/บริการให้คำปรึกษาแก่ชุมชนและวิทยากร</span></div>\r\n\r\n			<div>&nbsp;</div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &gt;&gt; สำนักงานคณะวิทยาการสื่อสาร มหาวิทยาลัยสงขลานครินทร์ วิทยาเขตปัตตานี</span></div>\r\n\r\n			<div>&nbsp;</div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;โทรศัพท์/โทรสาร 0 7334 9692&nbsp; Website:http://www.comm-sci.pn.psu.ac.th</span></div>\r\n\r\n			<div><span style="font-size:12px">&nbsp; &nbsp; &nbsp; &nbsp;</span></div>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n', 0, '', '2016-11-17 10:18:15', 1, 5, 1450344200, 5, 1450344200, NULL, NULL, NULL, NULL);
